mapper.select:
SELECT *
  FROM schedule
 WHERE c_code = #{c_code}
   AND u_id = #{u_id}
   AND To_char(s_start, 'yyyymmdd') = To_char(sysdate, 'yyyymmdd')
   AND s_stat = 'P'

SELECT *
  FROM schedule
 WHERE s_code = #{s_code} 
   AND u_id = #{u_id}

mapper.update:
UPDATE schedule
   SET s_arrive = sysdate
 WHERE s_code = #{s_code}
   AND s_arrive IS NULL 
   AND c_code = #{c_code}

UPDATE schedule
   SET s_leave = sysdate
 WHERE s_code = #{s_code}
   AND s_leave IS NULL 
   AND c_code = #{c_code}

mapper.insert:
INSERT INTO schedule
            (s_code
             , c_code
             , u_id
             , s_start
             , s_end

			 , sr_code
			 )
     VALUES ( seq_s_code.nextval
              , #{c_code}
              , #{u_id}
              , To_date( #{s_start}, 'YYYY-MM-DD HH24:MI')
              , To_date( #{s_end}, 'YYYY-MM-DD HH24:MI') 

			  , #{sr_code}
			  )

mapper.update:
UPDATE schedule
   SET s_start = To_date( #{s_start}, 'YYYY-MM-DD HH24:MI')
       , s_end = To_date( #{s_end}, 'YYYY-MM-DD HH24:MI') 
 WHERE s_code = #{s_code}
   AND u_id = #{u_id}
   AND s_stat = 'W'

mapper.delete:
DELETE FROM schedule
 WHERE s_code = #{s_code} 
   AND u_id = #{u_id}
   AND s_stat = 'W'

mapper.update:
UPDATE schedule
   SET s_stat = 'D'
 WHERE u_id = #{u_id}
   AND s_code = #{s_code}

UPDATE schedule
   SET s_stat = 'P'
 WHERE u_id = #{u_id}
   AND s_code = #{s_code}

mapper.select:
SELECT *
  FROM schedule
 WHERE To_char(s_start, 'd') = #{sr_code}
   AND u_id = #{u_id}
   AND c_code = #{c_code}
   AND s_end > sysdate

mapper.delete:
DELETE FROM schedule
 WHERE sr_code = #{sr_code}
   AND s_start > sysdate

mapper.select:

SELECT *
  FROM schedule
 WHERE c_code = #{c_code}
   AND u_id = #{u_id}
   AND s_start >= To_date( #{s_start}, 'yyyy-mm-dd hh24:mi')
   AND s_start < To_date( #{s_end}, 'yyyy-mm-dd hh24:mi')
