mapper.update:
		
			UPDATE salary
			SET    sa_wextra = #{sa_wextra}
			WHERE  s_code IN
			       (
			              SELECT s_code
			              FROM   schedule
			              WHERE  c_code = #{c_code}
					             AND    u_id = #{u_id}
					             AND    s_start>= (trunc(SYSDATE) - (interval '8' day))
					             AND    s_start < (trunc(SYSDATE) - (interval '1' day))
	               )
