mapper.insert:
INSERT INTO userinfo
            (u_id
             , u_pw
             , u_name
             , u_email
             , u_gender
             , u_birth
             , u_addr
             , u_phone
             , u_position)
     VALUES ( #{u_id}
     		  , 
              , #{u_name}
              , #{u_email}
              , #{u_gender}
              , #{u_birth}
              , #{u_addr}
              , #{u_phone}
              , #{u_position} )

mapper.select:
SELECT u_id
  FROM userinfo
 WHERE u_name = #{u_name}
   AND u_email = #{u_email}

SELECT Count(u_id)
  FROM userinfo
 WHERE u_name = #{u_name}
   AND u_email = #{u_email}

SELECT Count(u_id)
  FROM userinfo
 WHERE u_id = #{u_id}
   AND u_name = #{u_name}
   AND u_email = #{u_email}

SELECT Count(u_id)
  FROM userinfo
 WHERE u_id = #{u_id}

mapper.update:
